import{l as i,c as a,b as e,ai as r}from"./chunks/framework.DfOqixww.js";const o="/img/2025-02-09-12-18-17.png",t="/img/2025-02-09-12-22-47.png",n="/img/2025-02-09-12-34-28.png",s="/img/2025-02-09-12-37-08.png",h="/img/2025-02-09-12-39-37.png",m="/img/2025-02-09-12-43-59.png",c="/img/2025-02-09-12-49-20.png",k=JSON.parse('{"title":"蔬菜","description":"","frontmatter":{},"headers":[],"relativePath":"favorite/cook/vegetable.md","filePath":"favorite/cook/vegetable.md"}'),u={name:"favorite/cook/vegetable.md"};function d(f,l,p,_,b,g){return e(),a("div",null,l[0]||(l[0]=[r('<h1 id="蔬菜" tabindex="-1">蔬菜 <a class="header-anchor" href="#蔬菜" aria-label="Permalink to &quot;蔬菜&quot;">​</a></h1><h3 id="○-小锅米线-from-奔跑的椰子呀" tabindex="-1">○ 小锅米线（from 奔跑的椰子呀） <a class="header-anchor" href="#○-小锅米线-from-奔跑的椰子呀" aria-label="Permalink to &quot;○ 小锅米线（from 奔跑的椰子呀）&quot;">​</a></h3><ul><li>冷水，下一勺猪油、酸菜、番茄片</li><li>水开，下肉沫</li><li>加生抽、老抽、食用盐，调好咸淡</li><li>下米线，加韭菜段、豆芽、红油辣子，出锅</li></ul><h3 id="○-凉拌莴笋-from-奔跑的椰子呀" tabindex="-1">○ 凉拌莴笋（from 奔跑的椰子呀） <a class="header-anchor" href="#○-凉拌莴笋-from-奔跑的椰子呀" aria-label="Permalink to &quot;○ 凉拌莴笋（from 奔跑的椰子呀）&quot;">​</a></h3><ul><li>莴笋切丝，加一点盐杀水，拌匀，腌十分钟，腌好后挤水</li><li>准备调料，蒜末、小米辣、生抽、红油</li><li>调料倒入莴笋中，拌匀</li></ul><h3 id="○-番茄滑肉汤-from-奔跑的椰子呀" tabindex="-1">○ 番茄滑肉汤（from 奔跑的椰子呀） <a class="header-anchor" href="#○-番茄滑肉汤-from-奔跑的椰子呀" aria-label="Permalink to &quot;○ 番茄滑肉汤（from 奔跑的椰子呀）&quot;">​</a></h3><ul><li>梅花肉切片，加蒜末、食用盐、花椒面、红薯淀粉，加凉水，抓匀（较干裹匀）</li><li>热锅，高油温下番茄丁出沙，加水，加生抽、食用盐。水开后，多煮 2-3 分钟</li><li>最小火，下豆芽、滑肉。定型后推一推，再煮 2 分钟左右</li><li>加葱花出锅</li></ul><h3 id="○-番茄鸡蛋汤-from-奔跑的椰子呀" tabindex="-1">○ 番茄鸡蛋汤（from 奔跑的椰子呀） <a class="header-anchor" href="#○-番茄鸡蛋汤-from-奔跑的椰子呀" aria-label="Permalink to &quot;○ 番茄鸡蛋汤（from 奔跑的椰子呀）&quot;">​</a></h3><ul><li>热锅，高油温下番茄出沙，加水</li><li>水开将番茄煮软，2 分钟左右，加足盐</li><li>2 鸡蛋搅匀，快速转圈倒入，立马关火等 10s，撒葱花，筷子轻轻拨动，完成</li></ul><h3 id="○-番茄炒蛋" tabindex="-1">○ 番茄炒蛋 <a class="header-anchor" href="#○-番茄炒蛋" aria-label="Permalink to &quot;○ 番茄炒蛋&quot;">​</a></h3><p><img src="'+o+'" alt=""></p><ul><li>番茄，头尾十字花刀，开水烫番茄，去皮</li><li>番茄去芯，横切竖切成块，不要太小</li><li>3 个鸡蛋，加盐、水淀粉，搅匀</li><li>润锅</li><li>3 成油温大火下鸡蛋（比较热，更加蓬松），两面金黄色，捣烂倒出</li><li>炒番茄，炒融，加蒜米、番茄沙司，加鸡蛋</li><li>加少许盐、白糖，加少许水，加葱花，出锅</li></ul><h3 id="○-酸辣土豆丝" tabindex="-1">○ 酸辣土豆丝 <a class="header-anchor" href="#○-酸辣土豆丝" aria-label="Permalink to &quot;○ 酸辣土豆丝&quot;">​</a></h3><p><img src="'+t+'" alt=""></p><ul><li>土豆先宽面切一刀可立稳，切片，扑克牌码开，切丝</li><li>土豆丝清洗</li><li>水开，加盐，焯土豆丝，加白醋更脆，一小会就行不要太软</li><li>润锅</li><li>加干辣椒节、葱段爆香，快速下土豆丝翻炒</li><li>加少许盐、白糖、生抽，锅边加陈醋，加葱花，出锅</li></ul><h3 id="○-清炒丝瓜" tabindex="-1">○ 清炒丝瓜 <a class="header-anchor" href="#○-清炒丝瓜" aria-label="Permalink to &quot;○ 清炒丝瓜&quot;">​</a></h3><p><img src="'+n+'" alt=""></p><ul><li>清水加盐，泡改刀的丝瓜 5-6 分钟，避免发黑</li><li>润锅倒出，热锅凉油</li><li>下切好的姜片、蒜片、葱段、不辣红椒，下丝瓜</li><li>加少许盐、白糖，加蚝油，锅边加一丢丢水，中火 30s</li><li>加少许水淀粉，出锅</li></ul><h3 id="○-青椒炒茄子" tabindex="-1">○ 青椒炒茄子 <a class="header-anchor" href="#○-青椒炒茄子" aria-label="Permalink to &quot;○ 青椒炒茄子&quot;">​</a></h3><p><img src="'+s+'" alt=""></p><ul><li>茄子切片，锅热中火干煸，蔫了倒出</li><li>加菜籽油、猪油，加蒜蓉、几篇姜、青椒丝，加豆豉，加茄子翻炒</li><li>加少许白糖、一点盐、生抽、蚝油，出锅</li></ul><h3 id="○-蒜蓉油麦菜" tabindex="-1">○ 蒜蓉油麦菜 <a class="header-anchor" href="#○-蒜蓉油麦菜" aria-label="Permalink to &quot;○ 蒜蓉油麦菜&quot;">​</a></h3><p><img src="'+h+'" alt=""></p><ul><li>水开，加油，油麦菜段焯水，40s 左右</li><li>润锅倒出，热锅凉油</li><li>加蒜蓉炒香，快速下青菜</li><li>加少许盐、一点白糖、一点生抽，加少许水淀粉，出锅</li></ul><h3 id="○-蚝油菠菜" tabindex="-1">○ 蚝油菠菜 <a class="header-anchor" href="#○-蚝油菠菜" aria-label="Permalink to &quot;○ 蚝油菠菜&quot;">​</a></h3><p><img src="'+m+'" alt=""></p><ul><li>水开，加油盐，下菠菜（先根部再整个），熟了捞出沥干</li><li>润锅倒出，热锅凉油</li><li>加蒜米、干葱末炒香，加两圆勺清水（淋菠菜）</li><li>加少许盐、一点白糖、生抽、一点蚝油，加少许水淀粉（一边倒一边滑匀至微稠），出锅淋菠菜</li></ul><h3 id="○-干锅花菜" tabindex="-1">○ 干锅花菜 <a class="header-anchor" href="#○-干锅花菜" aria-label="Permalink to &quot;○ 干锅花菜&quot;">​</a></h3><p><img src="'+c+'" alt=""></p><ul><li>5 成油温油炸花菜，迅速捞出（熟且有脆度）</li><li>加油、小火煸五花肉，锅边加花雕酒，加拍破的蒜头、小米椒、干辣椒段、干花椒</li><li>加一点老抽、小半勺蚝油，下花菜翻炒，锅边加一点点水，盖上中火一分钟</li><li>加盐、白糖、蒜苗叶，加少许水淀粉，加一点芝麻香油，出锅</li></ul>',30)]))}const P=i(u,[["render",d]]);export{k as __pageData,P as default};
